#!/usr/bin/env python# -*- coding: utf-8 -*-# Created by spider3 on 2018/5/9# Copyright (c) 2018 spider3. All rights reserved.import jsonfrom utils.save2mysql import save2mysqldef handle_haier():    with open("./haier_new103096-1.json", "r") as f:        data = f.read()        data = json.loads(data)        for one_data in data:            print(one_data)            one_data['phone_number'] = one_data['shopPhone'] if one_data['shopPhone'] else ""            one_data['address'] = one_data["shopAddress"]            one_data["name"] = one_data["shopName"]    save2mysql("localhost", "root", "root", "my_test", "feigo_shops", data)def main():    handle_haier()if __name__ == '__main__':    main()