#!/usr/bin/env python# -*- coding: utf-8 -*-# Created by spider3 on 2018/5/10# Copyright (c) 2018 spider3. All rights reserved.import pymysqldef main():    connection = pymysql.connect(host='localhost',                                 user='root',                                 password='root',                                 db='qianxungou',                                 port=3306,                                 charset='utf8')    with connection.cursor() as cursor:        sql = "SELECT `name` FROM feigo_regions WHERE `name` IS NOT NULL "        cursor.execute(sql)        data = cursor.fetchall()        data = [one_data[0] for one_data in data ]    li = []    chongfu = []    for region in data:        if region in li:            chongfu.append(region)        else:            li.append(region)    print(chongfu)if __name__ == '__main__':    main()