#!/usr/bin/env python# -*- coding: utf-8 -*-# Created by spider3 on 2018/3/7# Copyright (c) 2018 spider3. All rights reserved.import requestsimport jsonclass BaiduCoord(object):    def __init__(self,address):        self.address = address    # 获取百度shi十字坐标    def get_ten_coord(self):        data = {"qt": "gc", "wd": self.address}        response = requests.get("http://api.map.baidu.com", params=data)        response_json = json.loads(response.text)        # print(response_json)        coord = response_json["content"]["coord"]        self.ten_coord = coord        return coord    # 根据百度十字坐标转换成经纬度坐标    def get_jw_coord(self):        coord = self.ten_coord["x"] + "," + self.ten_coord["y"]        data = {"from": 6, "to": 5, "ak": "mDml72vqmPtHmZHl7tus153lx9e1pg8A", "coords": coord}        response = requests.get("http://api.map.baidu.com/geoconv/v1/", params=data)        response_json = json.loads(response.text)        if response_json["status"] == 0:            return response_json["result"][0]def main():    bc = BaiduCoord(address="广州市番禺迎宾路万博中心吉盛伟邦I馆103-1")    print(bc.get_ten_coord())    print(bc.get_jw_coord())if __name__ == '__main__':    main()