#!/usr/bin/env python# -*- coding: utf-8 -*-# Created by spider3 on 2018/3/12# Copyright (c) 2018 spider3. All rights reserved.import loggingdef init_logger(path="./log.txt"):    logger = logging.getLogger()    fh = logging.FileHandler(path)    fh.setLevel(logging.DEBUG)    logger.setLevel(logging.INFO)  # Log等级总开关    logger.addHandler(fh)    formatter = logging.Formatter("%(asctime)s - %(filename)s[line:%(lineno)d] - %(levelname)s: %(message)s")    fh.setFormatter(formatter)    return logger    # logger.debug('this is a logger debug message')    # logger.info('this is a logger info message')    # logger.warning('this is a logger warning message')    # logger.error('this is a logger error message')    # logger.critical('this is a logger critical message')def main():    init_logger("./log.txt")if __name__ == '__main__':    main()