#!/usr/bin/env python# -*- coding: utf-8 -*-# Created by spider3 on 2018/6/15# Copyright (c) 2018 spider3. All rights reserved.import requestsimport jsondef get_address():    url = 'http://www.yuexing.com/index.php?act=map&do=mall'    response = requests.get(url)    html = response.text[14:-1]    dict_html = json.loads(html)    address_list = dict_html["list"]    print(address_list)    with open("./月星家居数据.json", "w", encoding="utf-8") as f:        f.write(json.dumps(address_list, ensure_ascii=False))def main():    get_address()if __name__ == '__main__':    main()